def write_help_files():
    if not os.path.exists("help/"):
        os.makedirs("help/")
    for name in help_files:
        f = open("help/"+name+".txt", "w")
        for line in help_files[name]:
            f.write(line + "\n")
        f.close()

credentials = []

def write_plugins():
    create = True
    if os.path.exists("plugins/"):
        print("Found a plugins folder, do you want to wipe it?")
        if input().lower() != "yes":
            create = False
        else:
            shutil.rmtree('plugins/')

    if create:
        os.makedirs("plugins/")

    print("creating init file..")
    f = open("plugins/__init__.py", "w")
    f.write("")
    f.close()

def write_all():
    print(" THIS WILL RESET ALL FILES TO DEFAULT, Type yes to continue.")
    response = input().lower()
    if response == "yes":
        cred = True
        if os.path.exists("credentials.txt"):
            print(" Already found credentials, do you want to re-enter them?")
            response = input().lower()
            if response != "yes":
                cred = False
        if cred:
            print(" Enter the bots token, or type no to enter a username and password instead.")
            result = input()
            if result.lower() == "no":
                print(" Enter the bots username (email):")
                credentials.append("username=" + input())
                print(" Enter the bots password:")
                credentials.append("password=" + input())
            else:
                credentials.append("token=" + result)
                print(" Enter the bot ID to create server auth link, or enter no to skip")
                result = input()
                if result.lower() != "no":
                    print(" Enter bot permissions number (https://discordapp.com/developers/docs/topics/permissions):")
                    print(discord.utils.oauth_url(result, permissions=discord.Permissions(int(input()))))
            print("creating credentials file...")
            f = open("credentials.txt", "w")
            for line in credentials:
                f.write(line+"\n")
            f.close()

        write_plugins()

        if os.path.exists("help/"):
            print(" Override help files?")
            if input().lower() == "yes":
                print("creating help files...")
                write_help_files()
        else:
            print("creating help files...")
            write_help_files()

        if os.path.exists("playlists/"):
            print(" Clear playlists?")
            response = input().lower()
            if response == "yes":
                shutil.rmtree('playlists/')

        print("creating folders...")
        if not os.path.exists("data/"):
            os.makedirs("data/")
        if not os.path.exists("playlists/"):
            os.makedirs("playlists/")


        print("creating normal files...")
        for file in files:
            f = open(file, "w")
            f.write("")
            f.close()
        print("creating permissions file...")
        f = open("data/permissions.txt", "w")
        f.write("DEFAULT=+all\n")
        f.close()
        print("creating activity events file...")
        f = open("data/activity_events.txt", "w")
        for line in events:
            f.write(line+"\n")
        f.close()
        print("creating settings file...")
        f = open("data/settings.txt", "w")
        for line in settings:
            f.write(line+"\n")
        f.close()
        print("creating hangman file...")
        f = open("hangman.txt", "w")
        for line in hangman_str:
            f.write(line+"\n")
        f.close()

def run():
    print("Enter setup type, or just press enter to do first time setup:")
    print("   Types: all, help, playlists, bot, plugins")
    setup_type = input().lower().replace(" ", "")

    if setup_type == "all" or setup_type == "":
        write_all()
        print("done!, type something to return to the start")
        if input().replace(" ", "") != "":
            run()
    elif setup_type == "help":
        print(" This will override all of you help files, type yes to continue")
        if input().lower() == "yes":
            print("writing help files...")
            write_help_files()
            print("done!, type something to return to the start")
            if input().replace(" ", "") != "":
                run()
        else:
            run()
    elif setup_type == "plugins":
        write_plugins()
        print("done!, type something to return to the start")
        if input().replace(" ", "") != "":
            run()
    elif setup_type == "playlists":
        print(" This will clear your playlists, are you sure?")
        if input().lower() == "yes":
            print("clearing the playlist folder...")
            shutil.rmtree('playlists/')
            print("creating the new playlist folder..")
            os.makedirs("playlists/")
            if not os.path.exists("data/"):
                os.makedirs("data/")
            print("creating the new collections file...")
            f = open("data/collections.txt", "w")
            f.write("")
            f.close()
            print("done!, type something to return to the start")
            if input().replace(" ", "") != "":
                run()
        else:
            run()
    elif setup_type == "bot":
        print(" This will override your credentials, are you sure?")
        if input().lower() == "yes":
            print(" Enter the bots token, or type no to enter a username and password instead.")
            result = input()
            if result.lower() == "no":
                print(" Enter the bots username (email):")
                credentials.append("username=" + input())
                print(" Enter the bots password:")
                credentials.append("password=" + input())
            else:
                credentials.append("token=" + result)
                print(" Enter the bot ID to create server auth link, or enter no to skip")
                result = input()
                if result.lower() != "no":
                    print(" Enter bot permissions number (https://discordapp.com/developers/docs/topics/permissions):")
                    print(discord.utils.oauth_url(result, permissions=discord.Permissions(int(input()))))
            print("creating credentials file...")
            f = open("credentials.txt", "w")
            for line in credentials:
                f.write(line+"\n")
            f.close()
            print("done!, type something to return to the start")
            if input().replace(" ", "") != "":
                run()
        else:
            run()
